CREATE database IF NOT exists cinego;

CREATE TABLE IF NOT EXISTS users
(
	id INT NOT NULL AUTO_INCREMENT,
    email VARCHAR(255),
    motdepasse VARCHAR(255),
    PRIMARY KEY (id)
);
CREATE TABLE IF NOT EXISTS movies
(
	id INT(2) NOT NULL AUTO_INCREMENT,
    genre VARCHAR(255),
    title VARCHAR(50),
    yproduction INT(4),
    temps INT(3),
    director TEXT,
    urlimage TEXT,
    PRIMARY KEY (id)
)ENGINE=InnoDB; 
