CREATE TABLE IF NOT EXISTS seances
(
	id INT(2) NOT NULL AUTO_INCREMENT,
    PRIMARY KEY (id),
    moviesid INT,
   horraire DATE,
   CONSTRAINT fk_movie_id
	FOREIGN KEY (moviesid)
    REFERENCES movies(id)
);
CREATE TABLE IF NOT EXISTS reservations
(
	id INT(2) NOT NULL AUTO_INCREMENT,
    PRIMARY KEY (id),
    seancesid INT,
    moviesid INT,
    sallesid INT,
    userid INT,
CONSTRAINT fk_sence_id
	FOREIGN KEY (seancesid)
    REFERENCES seances(id),
CONSTRAINT fk_movie_res_id
	FOREIGN KEY (moviesid)
    REFERENCES movies(id),
CONSTRAINT fk_salle_id
	FOREIGN KEY (sallesid)
    REFERENCES salles(id),
CONSTRAINT fk_user_id
	FOREIGN KEY (userid)
    REFERENCES users(id)
);

